import { StyleSheet, Text, View, Button, Image } from 'react-native';
import { useEffect, useState } from 'react';

export default function MovieDetailScreen({route, navigation}) {
  const {id, title, release_date, poster_path, overview} = route.params;
  useEffect(() => console.log("here"),[]);
  return (
    <View style={styles.container}>
        <Text style={styles.title}>{title}</Text>
        <Text style={styles.date}>{release_date}</Text>
        <Image
              style={styles.img}
              source={{
                uri: 'https://image.tmdb.org/t/p/w400/' + poster_path,
              }}
            />
        <Text style={styles.h2}>Overview</Text>
        <Text style={styles.bread}>{overview}</Text>
        <Button
            title="Go Back"
            onPress={() => navigation.navigate('Movies')}
          />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#fff',
    justifyContent: "center",
    textAlign: "center",
    alignItems: "center",
  },
  title: {
    marginTop: 20,
    fontSize: 30,
    marginBottom: 10,
    fontWeight: "bold",
  },
  img: {
    width: "300px",
    height: "450px",
    border: "1px solid #FFF",
    borderRadius: 15,
  },
  date: {
    fontSize: "12",
    color: "#999",
    marginBottom: 14,
  },
  h2: {
    fontSize: 20,
    textAlign: 'left',
  },
  bread: {
    marginLeft: 20,
    textAlign: "left",
    fontSize: 14,
  }
});
