import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, Button, Box } from 'react-native';
import { useEffect, useState } from 'react';
import { SafeAreaView } from 'react-native-safe-area-context';
import { FlatList, SectionList } from 'react-native-web';

export default function MoviesList({navigation}) {

  const [data, setData] = useState([]);

  useEffect(() => {
    fetchMovies();
  }, []);

  const fetchMovies = async() => {
    const respone = await fetch("https://jsonplaceholder.typicode.com/todos/");
    const data = await respone.json();
    setData(data);
  }

  const renderItem = ({ item }) => {
    return (
      <Box px={5} py={2} rounded="md" bg="primary.300" my={2}>
        {item.title}
      </Box>
    );
  };

  return (
    <View style={{ alignItems: 'center', justifyContent: 'center' }}>
        <SafeAreaView>
            <FlatList
            data={data}
            renderItem={renderItem}
            keyExtractor={(item) => item.id.toString()}
            />
        </SafeAreaView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
