import React, { useState, useEffect } from "react";
import { FlatList, Text, View, StyleSheet } from "react-native";
import { SafeAreaView } from "react-native-safe-area-context";

const Item = ({original_title}) => (
  <View style={styles.item}>
    <Text>{original_title}</Text>
  </View>
);

export default function MoviesList() {
  const [data, setData] = useState([]);

  const fetchData = async () => {
    //const resp = await fetch("https://jsonplaceholder.typicode.com/todos/");
    const resp = await fetch("https://api.themoviedb.org/3/movie/popular?api_key=aeb288a4571b8ddc0f7a60cc616d7512&language=en-US&page=1");
    const data = await resp.json();
    setData(data);
  };

  useEffect(() => {
    fetchData();
  }, []);

  const renderItem = ({ item }) => <Item original_title={item.original_title}/>

  return (
    <SafeAreaView>
      <FlatList
        data={data}
        renderItem={renderItem}
        keyExtractor={(item) => item.id}
      />
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  item: {
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 10,
  },
});
